import Review from "./Review/Review";
import {useEffect, useState} from "react";
import axios from "axios";

const Reviews = () => {
    let [reviews, setReviews] = useState([
        {
            userId: 'Tom',
            id: 1,
            title: "Good product",
            body: "I liked this product so much."
        },
        {
            userId: 'Stephanie',
            id: 1,
            title: "Medium quality product",
            body: "I liked this product but it has some bugs."
        },
        {
            userId: 'Megan',
            id: 1,
            title: "Don't like it",
            body: "it didn't work."
        },
    ]);
    // useEffect(() => {
    //     axios.get('https://jsonplaceholder.typicode.com/posts')
    //         .then(response => setReviews(response.data))
    // }, []);
    let [currentReview, setCurrentReview] = useState();

    const currentReviewHandler = (e) => {
        let currentReview = e.currentTarget.value;
        setCurrentReview(currentReview);
    }

    const addReviewHandler = () => {
        let review = {
            userId: 'Nick',
            id: 4,
            title: "Great product",
            body: "I liked this product."
        }
    }

    return (
        <div className="Reviews">
            <h2>Add a review:</h2>
            <textarea name="" id="" cols="30" rows="10" onChange={currentReviewHandler}></textarea>
            <button onClick={addReviewHandler}>Add review</button>
            <h2>Reviews:</h2>
            {
                reviews.map(review => <Review review={review}/>)
            }
        </div>
    )
}

export default Reviews;
